import Flicking, { EVENTS, Plugin } from "@egjs/flicking";

/**
 * You can apply parallax effect while panel is moving.
 * @ko 패널들을 움직이면서 parallax 효과를 부여할 수 있습니다.
 * @memberof Flicking.Plugins
 */
class Parallax implements Plugin {
  private _flicking: Flicking | null;

  /* Options  */
  private _selector: string;
  private _scale: number;

  public get selector() { return this._selector; }
  public get scale() { return this._scale; }

  public set selector(val: string) { this._selector = val; }
  public set scale(val: number) { this._scale = val; }

  /**
   * @param {string} selector Selector of the element to apply parallax effect<ko> Parallax 효과를 적용할 엘리먼트의 선택자 </ko>
   * @param {number} scale Effect amplication scale<ko>효과 증폭도</ko>
   * @example
   * ```ts
   * flicking.addPlugins(new Parallax("img", 1));
   * ```
   */
  public constructor(selector = "", scale = 1) {
    this._flicking = null;
    this._selector = selector;
    this._scale = scale;
  }

  public init(flicking: Flicking): void {
    if (this._flicking) {
      this.destroy();
    }

    this._flicking = flicking;

    flicking.on(EVENTS.MOVE, this._onMove);
    flicking.on(EVENTS.AFTER_RESIZE, this.update);
    this._onMove();
  }

  public destroy(): void {
    if (!this._flicking) return;

    this._flicking.off(EVENTS.MOVE, this._onMove);
    this._flicking.off(EVENTS.AFTER_RESIZE, this.update);
    this._flicking = null;
  }

  public update = (): void => {
    this._onMove();
  };

  private _onMove = (): void => {
    const flicking = this._flicking;

    if (!flicking) return;

    const panels = flicking.visiblePanels;

    panels.forEach(panel => {
      const progress = panel.outsetProgress;
      const el = panel.element;
      const target = this._selector ? el.querySelector<HTMLElement>(this._selector)! : el;
      const parentTarget = target.parentNode as Element;
      const rect = target.getBoundingClientRect();
      const parentRect = parentTarget.getBoundingClientRect();
      const position = (parentRect.width - rect.width) / 2 * progress * this._scale;
      const transform = `translate(-50%) translate(${position}px)`;
      const style = target.style;

      style.cssText += `transform: ${transform};-webkit-transform: ${transform};-ms-transform:${transform}`;
    });
  };
}
export default Parallax;
import Flicking, { EVENTS, Plugin } from "@egjs/flicking";

/**
 * You can apply fade in / out effect while panel is moving.
 * @ko 패널들을 움직이면서 fade in / out 효과를 부여할 수 있습니다.
 * @memberof Flicking.Plugins
 */
class Fade implements Plugin {
  private _flicking: Flicking | null;

  /* Options  */
  private _selector: string;
  private _scale: number;

  public get selector() { return this._selector; }
  public get scale() { return this._scale; }

  public set selector(val: string) { this._selector = val; }
  public set scale(val: number) { this._scale = val; }

  /**
   * @param - The selector of the element to which the fade effect is to be applied. If the selector is blank, it applies to panel element. <ko>Fade 효과를 적용할 대상의 선택자. 선택자가 공백이면 패널 엘리먼트에 적용된다.</ko>
   * @param - Effect amplication scale<ko>효과 증폭도</ko>
   * @example
   * ```ts
   * flicking.addPlugins(new Fade("p", 1));
   * ```
   */
  public constructor(selector = "", scale = 1) {
    this._flicking = null;
    this._selector = selector;
    this._scale = scale;
  }

  public init(flicking: Flicking): void {
    if (this._flicking) {
      this.destroy();
    }

    this._flicking = flicking;

    flicking.on(EVENTS.MOVE, this._onMove);
    flicking.on(EVENTS.AFTER_RESIZE, this.update);
    this._onMove();
  }

  public destroy(): void {
    if (!this._flicking) return;

    this._flicking.off(EVENTS.MOVE, this._onMove);
    this._flicking.off(EVENTS.AFTER_RESIZE, this.update);
    this._flicking = null;
  }

  public update = (): void => {
    this._onMove();
  };

  private _onMove = (): void => {
    const flicking = this._flicking;
    const selector = this._selector;
    const scale = this._scale;

    if (!flicking) return;

    const panels = flicking.visiblePanels;

    panels.forEach(panel => {
      const progress = panel.outsetProgress;
      const el = panel.element;
      const target = selector ? el.querySelector<HTMLElement>(selector)! : el;
      const opacity = Math.min(1, Math.max(0, (1 - Math.abs(progress * scale))));

      target.style.opacity = `${opacity}`;
    });
  };
}

export default Fade;
import Flicking, { EVENTS, Plugin, DIRECTION } from "@egjs/flicking";

interface AutoPlayOptions {
  duration: number;
  direction: typeof DIRECTION["NEXT"] | typeof DIRECTION["PREV"];
  stopOnHover: boolean;
}

/**
 * Plugin that allow you to automatically move to the next/previous panel, on a specific time basis
 * @ko 일정 시간마다, 자동으로 다음/이전 패널로 넘어가도록 할 수 있는 플러그인
 * @memberof Flicking.Plugins
 */
class AutoPlay implements Plugin {
  /* Options */
  private _duration: AutoPlayOptions["duration"];
  private _direction: AutoPlayOptions["direction"];
  private _stopOnHover: AutoPlayOptions["stopOnHover"];

  /* Internal Values */
  private _flicking: Flicking | null = null;
  private _timerId = 0;
  private _mouseEntered = false;

  public get duration() { return this._duration; }
  public get direction() { return this._direction; }
  public get stopOnHover() { return this._stopOnHover; }

  public set duration(val: number) { this._duration = val; }
  public set direction(val: AutoPlayOptions["direction"]) { this._direction = val; }
  public set stopOnHover(val: boolean) { this._stopOnHover = val; }

  /**
   * @param {AutoPlayOptions} options Options for the AutoPlay instance.<ko>AutoPlay 옵션</ko>
   * @param {number} options.duration Time to wait before moving on to the next panel.<ko>다음 패널로 움직이기까지 대기 시간</ko>
   * @param {"PREV" | "NEXT"} options.direction The direction in which the panel moves.<ko>패널이 움직이는 방향</ko>
   * @param {boolean} options.stopOnHover Whether to stop when mouse hover upon the element.<ko>엘리먼트에 마우스를 올렸을 때 AutoPlay를 정지할지 여부</ko>
   * @example
   * ```ts
   * flicking.addPlugins(new AutoPlay({ duration: 2000, direction: "NEXT" }));
   * ```
   */
  public constructor({
    duration = 2000,
    direction = DIRECTION.NEXT,
    stopOnHover = false
  }: Partial<AutoPlayOptions> = {}) {
    this._duration = duration;
    this._direction = direction;
    this._stopOnHover = stopOnHover;
  }

  public init(flicking: Flicking): void {
    if (this._flicking) {
      this.destroy();
    }

    flicking.on({
      [EVENTS.MOVE_START]: this.stop,
      [EVENTS.HOLD_START]: this.stop,
      [EVENTS.MOVE_END]: this.play,
      [EVENTS.SELECT]: this.play
    });

    this._flicking = flicking;
    if (this._stopOnHover) {
      const targetEl = this._flicking.element;
      targetEl.addEventListener("mouseenter", this._onMouseEnter, false);
      targetEl.addEventListener("mouseleave", this._onMouseLeave, false);
    }

    this.play();
  }

  public destroy(): void {
    const flicking = this._flicking;

    this._mouseEntered = false;
    this.stop();

    if (!flicking) {
      return;
    }

    flicking.off(EVENTS.MOVE_START, this.stop);
    flicking.off(EVENTS.HOLD_START, this.stop);
    flicking.off(EVENTS.MOVE_END, this.play);
    flicking.off(EVENTS.SELECT, this.play);

    const targetEl = flicking.element;
    targetEl.removeEventListener("mouseenter", this._onMouseEnter, false);
    targetEl.removeEventListener("mouseleave", this._onMouseLeave, false);

    this._flicking = null;
  }

  public update(): void {
    // DO-NOTHING
  }

  public play = () => {
    const flicking = this._flicking;
    const direction = this._direction;

    if (!flicking) {
      return;
    }

    this.stop();

    if (this._mouseEntered || flicking.animating) {
      return;
    }

    this._timerId = window.setTimeout(() => {
      if (direction === DIRECTION.NEXT) {
        flicking.next().catch(() => void 0);
      } else {
        flicking.prev().catch(() => void 0);
      }

      this.play();
    }, this._duration);
  };

  public stop = () => {
    clearTimeout(this._timerId);
  };

  private _onMouseEnter = () => {
    this._mouseEntered = true;
    this.stop();
  };

  private _onMouseLeave = () => {
    this._mouseEntered = false;
    this.play();
  };
}

export default AutoPlay;
export const BROWSER = {
  CLICK: "click",
  MOUSE_DOWN: "mousedown",
  TOUCH_START: "touchstart"
};
export const ARROW = {
  PREV_SELECTOR: ".flicking-arrow-prev",
  NEXT_SELECTOR: ".flicking-arrow-next",
  DISABLED_CLASS: "flicking-arrow-disabled"
} as const;

export const PAGINATION = {
  SELECTOR: ".flicking-pagination",
  PREFIX: "flicking-pagination",
  BULLET_WRAPPER_SUFFIX: "bullets",
  BULLET_SUFFIX: "bullet",
  BULLET_ACTIVE_SUFFIX: "bullet-active",
  FRACTION_WRAPPER_SUFFIX: "fraction",
  FRACTION_CURRENT_SUFFIX: "fraction-current",
  FRACTION_TOTAL_SUFFIX: "fraction-total",
  SCROLL_UNINIT_SUFFIX: "uninitialized",
  SCROLL_WRAPPER_SUFFIX: "scroll",
  SCROLL_SLIDER_SUFFIX: "slider",
  SCROLL_PREV_SUFFIX: "bullet-prev",
  SCROLL_NEXT_SUFFIX: "bullet-next",
  TYPE: {
    BULLET: "bullet",
    FRACTION: "fraction",
    SCROLL: "scroll"
  }
} as const;

export const SYNC = {
  TYPE: {
    CAMERA: "camera",
    INDEX: "index"
  }
} as const;
export const addClass = (el: HTMLElement, className: string) => {
  if (!el) return;

  if (el.classList) {
    el.classList.add(className);
  } else {
    const classes = el.className.split(" ");

    if (classes.indexOf(className) < 0) {
      el.className = `${el.className} ${className}`;
    }
  }
};

export const removeClass = (el: HTMLElement, className: string) => {
  if (!el) return;

  if (el.classList) {
    el.classList.remove(className);
  } else {
    const classRegex = new RegExp(`( |^)${className}( |$)`, "g");
    el.className.replace(classRegex, " ");
  }
};

export const getElement = (selector: string, parent: HTMLElement, pluginName: string) => {
  const el = parent.querySelector(selector);

  if (!el) {
    throw new Error(`[Flicking-${pluginName}] Couldn't find element with the given selector: ${selector}`);
  }

  return el as HTMLElement;
};
import Flicking, { EVENTS, FlickingError, Plugin } from "@egjs/flicking";

import { BROWSER } from "./event";
import { ARROW } from "./const";
import { addClass, getElement, removeClass } from "./utils";

interface ArrowOptions {
  parentEl: HTMLElement | null;
  prevElSelector: string;
  nextElSelector: string;
  disabledClass: string;
  moveCount: number;
  moveByViewportSize: boolean;
}

/**
 * A plugin to easily create prev/right arrow button of Flicking
 * @ko 이전/다음 버튼을 쉽게 만들 수 있는 플러그인
 * @memberof Flicking.Plugins
 */
class Arrow implements Plugin {
  /* Internal Values */
  private _flicking: Flicking | null = null;
  private _prevEl: HTMLElement;
  private _nextEl: HTMLElement;

  /* Options */
  private _parentEl: ArrowOptions["parentEl"];
  private _prevElSelector: ArrowOptions["prevElSelector"];
  private _nextElSelector: ArrowOptions["nextElSelector"];
  private _disabledClass: ArrowOptions["disabledClass"];
  private _moveCount: ArrowOptions["moveCount"];
  private _moveByViewportSize: ArrowOptions["moveByViewportSize"];

  public get prevEl() { return this._prevEl; }
  public get nextEl() { return this._nextEl; }

  public get parentEl() { return this._parentEl; }
  public get prevElSelector() { return this._prevElSelector; }
  public get nextElSelector() { return this._nextElSelector; }
  public get disabledClass() { return this._disabledClass; }
  public get moveCount() { return this._moveCount; }
  public get moveByViewportSize() { return this._moveByViewportSize; }

  public set parentEl(val: ArrowOptions["parentEl"]) { this._parentEl = val; }
  public set prevElSelector(val: ArrowOptions["prevElSelector"]) { this._prevElSelector = val; }
  public set nextElSelector(val: ArrowOptions["nextElSelector"]) { this._nextElSelector = val; }
  public set disabledClass(val: ArrowOptions["disabledClass"]) { this._disabledClass = val; }
  public set moveCount(val: ArrowOptions["moveCount"]) { this._moveCount = val; }
  public set moveByViewportSize(val: ArrowOptions["moveByViewportSize"]) { this._moveByViewportSize = val; }

  public constructor({
    parentEl = null,
    prevElSelector = ARROW.PREV_SELECTOR,
    nextElSelector = ARROW.NEXT_SELECTOR,
    disabledClass = ARROW.DISABLED_CLASS,
    moveCount = 1,
    moveByViewportSize = false
  }: Partial<ArrowOptions> = {}) {
    this._parentEl = parentEl;
    this._prevElSelector = prevElSelector;
    this._nextElSelector = nextElSelector;
    this._disabledClass = disabledClass;
    this._moveCount = moveCount;
    this._moveByViewportSize = moveByViewportSize;
  }

  public init(flicking: Flicking): void {
    if (this._flicking) {
      this.destroy();
    }

    this._flicking = flicking;

    flicking.on(EVENTS.MOVE, this._onAnimation);

    const parentEl = this._parentEl ? this._parentEl : flicking.element;
    const prevEl = getElement(this._prevElSelector, parentEl, "Arrow");
    const nextEl = getElement(this._nextElSelector, parentEl, "Arrow");

    [BROWSER.MOUSE_DOWN, BROWSER.TOUCH_START].forEach(evt => {
      prevEl.addEventListener(evt, this._preventInputPropagation, { passive: true });
      nextEl.addEventListener(evt, this._preventInputPropagation, { passive: true });
    });

    prevEl.addEventListener(BROWSER.CLICK, this._onPrevClick);
    nextEl.addEventListener(BROWSER.CLICK, this._onNextClick);

    this._prevEl = prevEl;
    this._nextEl = nextEl;

    this.update();
  }

  public destroy(): void {
    const flicking = this._flicking;

    if (!flicking) {
      return;
    }

    flicking.off(EVENTS.MOVE, this._onAnimation);

    const prevEl = this._prevEl;
    const nextEl = this._nextEl;

    [BROWSER.MOUSE_DOWN, BROWSER.TOUCH_START].forEach(evt => {
      prevEl.removeEventListener(evt, this._preventInputPropagation);
      nextEl.removeEventListener(evt, this._preventInputPropagation);
    });

    prevEl.removeEventListener(BROWSER.CLICK, this._onPrevClick);
    nextEl.removeEventListener(BROWSER.CLICK, this._onNextClick);
    this._flicking = null;
  }

  public update(): void {
    this._updateClass(this._flicking!.camera.position);
  }

  private _preventInputPropagation = (e: Event) => {
    e.stopPropagation();
  };

  private _onPrevClick = () => {
    const flicking = this._flicking!;
    const camera = flicking.camera;
    const anchorPoints = camera.anchorPoints;

    if (flicking.animating || anchorPoints.length <= 0) return;

    const firstAnchor = anchorPoints[0];
    const moveCount = this._moveCount;

    if (this._moveByViewportSize) {
      flicking.control.moveToPosition(camera.position - camera.size, flicking.duration)
        .catch(this._onCatch);
    } else {
      if (flicking.circularEnabled) {
        let targetPanel = flicking.currentPanel;

        for (let i = 0; i < moveCount; i++) {
          targetPanel = targetPanel.prev()!;
        }

        targetPanel.focus().catch(this._onCatch);
      } else if (flicking.index > firstAnchor.panel.index) {
        flicking.moveTo(Math.max(flicking.index - moveCount, firstAnchor.panel.index))
          .catch(this._onCatch);
      } else if (camera.position > camera.range.min) {
        flicking.moveTo(flicking.index)
          .catch(this._onCatch);
      }
    }
  };

  private _onNextClick = () => {
    const flicking = this._flicking!;

    const camera = flicking.camera;
    const anchorPoints = camera.anchorPoints;

    if (flicking.animating || anchorPoints.length <= 0) return;

    const lastAnchor = anchorPoints[anchorPoints.length - 1];
    const moveCount = this._moveCount;

    if (this._moveByViewportSize) {
      flicking.control.moveToPosition(camera.position + camera.size, flicking.duration)
        .catch(this._onCatch);
    } else {
      if (flicking.circularEnabled) {
        let targetPanel = flicking.currentPanel;

        for (let i = 0; i < moveCount; i++) {
          targetPanel = targetPanel.next()!;
        }

        targetPanel.focus().catch(this._onCatch);
      } else if (flicking.index < lastAnchor.panel.index) {
        flicking.moveTo(Math.min(flicking.index + moveCount, lastAnchor.panel.index))
          .catch(this._onCatch);
      } else if (camera.position > camera.range.min) {
        flicking.moveTo(flicking.index)
          .catch(this._onCatch);
      }
    }
  };

  private _onAnimation = () => {
    const flicking = this._flicking!;
    const camera = flicking.camera;
    const controller = flicking.control.controller;

    if (flicking.holding) {
      this._updateClass(camera.position);
    } else {
      this._updateClass(controller.animatingContext.end);
    }
  };

  private _updateClass(pos: number) {
    const flicking = this._flicking!;
    const disabledClass = this._disabledClass;
    const prevEl = this._prevEl;
    const nextEl = this._nextEl;
    const cameraRange = flicking.camera.range;

    const stopAtPrevEdge = flicking.circularEnabled
      ? false
      : pos <= cameraRange.min;
    const stopAtNextEdge = flicking.circularEnabled
      ? false
      : pos >= cameraRange.max;

    if (stopAtPrevEdge) {
      addClass(prevEl, disabledClass);
    } else {
      removeClass(prevEl, disabledClass);
    }

    if (stopAtNextEdge) {
      addClass(nextEl, disabledClass);
    } else {
      removeClass(nextEl, disabledClass);
    }
  }

  private _onCatch = (err: Error) => {
    if (err instanceof FlickingError) return;
    throw err;
  };
}

export default Arrow;
import Flicking, { clamp, EVENTS } from "@egjs/flicking";
import type { MoveEndEvent, MoveEvent, MoveStartEvent, Plugin, WillChangeEvent, SelectEvent } from "@egjs/flicking";

import { SYNC } from "./const";
import { addClass, removeClass } from "./utils";

/**
 * @property {string} [type="camera"] Types of methods to synchronize between Flickings. "camera" will sync by camera position, and "index" will sync by panel index
 * @property {SychronizableFlickingOptions} [synchronizedFlickingOptions=[]]	Detailed options for syncing Flickings.
 */
export interface SyncOptions {
  type: typeof SYNC.TYPE.CAMERA | typeof SYNC.TYPE.INDEX;
  synchronizedFlickingOptions: SychronizableFlickingOptions[];
}

/**
 * @property {Flicking} flicking An instance of Flicking to sync
 * @property {boolean} [isClickable=false] By enabling this option, clicking the given Flicking's panel will change the given & other Flicking's index
 * @property {boolean} [isSlidable=false] By enabling this option, the given Flicking's scroll with mouse/touch input will change other Flicking's index. Only available for the index type
 * @property {string | undefined} [activeClass=undefined] An extra class for the panels when selected
 */
export interface SychronizableFlickingOptions {
  flicking: Flicking;
  isClickable?: boolean;
  isSlidable?: boolean;
  activeClass?: string;
}

/**
 * Plugin for synchronizing multiple flickings
 * @ko 다양한 형태로 Flicking들이 같이 움직이게 할 수 있습니다.
 * @memberof Flicking.Plugins
 */
class Sync implements Plugin {
  /* Internal Values */
  private _flicking: Flicking | null = null;

  /* Options */
  private _type: SyncOptions["type"];
  private _synchronizedFlickingOptions: SyncOptions["synchronizedFlickingOptions"];

  public get type() { return this._type; }
  public get synchronizedFlickingOptions() { return this._synchronizedFlickingOptions; }

  public set type(val: SyncOptions["type"]) {
    this._type = val;
  }

  public set synchronizedFlickingOptions(val: SyncOptions["synchronizedFlickingOptions"]) {
    this._synchronizedFlickingOptions = val;
  }

  /** */
  public constructor({
    type = SYNC.TYPE.CAMERA,
    synchronizedFlickingOptions = []
  }: Partial<SyncOptions> = {}) {
    this._type = type;
    this._synchronizedFlickingOptions = synchronizedFlickingOptions;
  }

  public init(flicking: Flicking): void {
    const synced = this._synchronizedFlickingOptions;

    if (this._flicking) {
      this.destroy();
    }

    this._flicking = flicking;

    this._addEvents();

    synced.forEach(options => {
      const { flicking: syncedFlicking } = options;

      this._updateClass(options, syncedFlicking.defaultIndex);
    });
  }

  public destroy(): void {
    const flicking = this._flicking;

    if (!flicking) {
      return;
    }

    this._removeEvents();

    this._flicking = null;
  }

  public update(): void {
    this._synchronizedFlickingOptions.forEach(options => {
      this._updateClass(options, options.flicking.index);
    });
  }

  private _preventEvent(fn: () => void) {
    this._removeEvents();
    fn();
    this._addEvents();
  }

  private _addEvents = (): void => {
    const type = this._type;
    const synced = this._synchronizedFlickingOptions;

    synced.forEach(({ flicking, isSlidable, isClickable }) => {
      if (type === SYNC.TYPE.CAMERA) {
        flicking.on(EVENTS.MOVE, this._onMove);
        flicking.on(EVENTS.MOVE_START, this._onMoveStart);
        flicking.on(EVENTS.MOVE_END, this._onMoveEnd);
      }
      if (type === SYNC.TYPE.INDEX && isSlidable) {
        flicking.on(EVENTS.WILL_CHANGE, this._onIndexChange);
        flicking.on(EVENTS.WILL_RESTORE, this._onIndexChange);
      }
      if (isClickable) {
        flicking.on(EVENTS.SELECT, this._onSelect);
      }
    });
  };

  private _removeEvents = (): void => {
    const type = this._type;
    const synced = this._synchronizedFlickingOptions;

    synced.forEach(({ flicking, isSlidable, isClickable }) => {
      if (type === SYNC.TYPE.CAMERA) {
        flicking.off(EVENTS.MOVE, this._onMove);
        flicking.off(EVENTS.MOVE_START, this._onMoveStart);
        flicking.off(EVENTS.MOVE_END, this._onMoveEnd);
      }
      if (type === SYNC.TYPE.INDEX && isSlidable) {
        flicking.off(EVENTS.WILL_CHANGE, this._onIndexChange);
        flicking.off(EVENTS.WILL_RESTORE, this._onIndexChange);
      }
      if (isClickable) {
        flicking.off(EVENTS.SELECT, this._onSelect);
      }
    });
  };

  private _onIndexChange = (e: WillChangeEvent): void => {
    const flicking = e.currentTarget;

    if (!flicking.initialized) {
      return;
    }

    this._synchronizeByIndex(flicking, e.index);
  };

  private _onMove = (e: MoveEvent): void => {
    const camera = e.currentTarget.camera;
    const progress = (camera.position - camera.range.min) / camera.rangeDiff;

    this._synchronizedFlickingOptions.forEach(({ flicking }) => {
      if (flicking === e.currentTarget) return;

      let targetPosition = 0;

      if (camera.position < camera.range.min) {
        targetPosition = camera.position;
      } else if (camera.position > camera.range.max) {
        targetPosition = flicking.camera.range.max + camera.position - camera.range.max;
      } else {
        targetPosition = flicking.camera.range.min + flicking.camera.rangeDiff * progress;
      }

      void flicking.camera.lookAt(targetPosition);
    });
  };

  private _onMoveStart = (e: MoveStartEvent): void => {
    this._synchronizedFlickingOptions.forEach(({ flicking }) => {
      if (flicking !== e.currentTarget) {
        flicking.disableInput();
      }
    });
  };

  private _onMoveEnd = (e: MoveEndEvent): void => {
    this._synchronizedFlickingOptions.forEach(({ flicking }) => {
      if (flicking !== e.currentTarget) {
        flicking.enableInput();
        flicking.control.updateInput();
      }
    });
  };

  private _onSelect = (e: SelectEvent): void => {
    void e.currentTarget.moveTo(e.index).catch(() => void 0);

    this._synchronizeByIndex(e.currentTarget, e.index);
  };

  private _synchronizeByIndex = (activeFlicking: Flicking, index: number): void => {
    const synchronizedFlickingOptions = this._synchronizedFlickingOptions;

    this._preventEvent(() => {
      synchronizedFlickingOptions.forEach(options => {
        // Active class should be applied same to the Flicking which triggered event
        this._updateClass(options, index);

        const { flicking } = options;
        if (flicking === activeFlicking) return;

        const targetIndex = clamp(index, 0, flicking.panels.length);

        if (flicking.animating) {
          // Reserve moveTo once previous animation is finished
          flicking.once(EVENTS.MOVE_END, () => {
            void flicking.moveTo(targetIndex).catch(() => void 0);
          });
        } else {
          void flicking.moveTo(targetIndex);
        }
      });
    });
  };

  private _updateClass = ({ flicking, activeClass }: SychronizableFlickingOptions, index: number): void => {
    if (!activeClass) return;

    flicking.panels.forEach(panel => {
      if (panel.index === index)  {
        addClass(panel.element, activeClass);
      } else {
        removeClass(panel.element, activeClass);
      }
    });
  };
}

