import Flicking, { EVENTS, Plugin } from "@egjs/flicking";

interface PerspectiveOptions {
  selector: string;
  scale: number;
  rotate: number;
  perspective: number;
}

/**
 * You can apply perspective effect while panel is moving.
 * @ko 패널들을 움직이면서 입체감을 부여할 수 있습니다.
 * @memberof Flicking.Plugins
 */
class Perspective implements Plugin {
  private _flicking: Flicking | null;

  /* Options  */
  private _selector: PerspectiveOptions["selector"];
  private _scale: PerspectiveOptions["scale"];
  private _rotate: PerspectiveOptions["rotate"];
  private _perspective: PerspectiveOptions["perspective"];

  public get selector() { return this._selector; }
  public get scale() { return this._scale; }
  public get rotate() { return this._rotate; }
  public get perspective() { return this._perspective; }

  public set selector(val: string) { this._selector = val; }
  public set scale(val: number) { this._scale = val; }
  public set rotate(val: number) { this._rotate = val; }
  public set perspective(val: number) { this._perspective = val; }

  /**
   * @param - The selector of the element to which the perspective effect is to be applied. If the selector is blank, it applies to panel element. <ko>입체 효과를 적용할 대상의 선택자. 선택자가 공백이면 패널 엘리먼트에 적용된다.</ko>
   * @param - Effect amplication scale.<ko>효과 증폭도</ko>
   * @param - Effect amplication rotate.<ko>회전 증폭도</ko>
   * @param - The value of perspective CSS property. <ko>css perspective 속성 값</ko>
   * @example
   * ```ts
   * flicking.addPlugins(new Perspective({ selector: "p", scale: 1, rotate: 1, perspective = 1000 }));
   * ```
   */
  public constructor({ selector = "", scale = 1, rotate = 1, perspective = 1000 }: Partial<PerspectiveOptions> = {}) {
    this._flicking = null;
    this._selector = selector;
    this._scale = scale;
    this._rotate = rotate;
    this._perspective = perspective;
  }

  public init(flicking: Flicking): void {
    if (this._flicking) {
      this.destroy();
    }

    this._flicking = flicking;

    flicking.on(EVENTS.MOVE, this._onMove);
    flicking.on(EVENTS.AFTER_RESIZE, this.update);
    this._onMove();
  }

  public destroy(): void {
    if (!this._flicking) return;

    this._flicking.off(EVENTS.MOVE, this._onMove);
    this._flicking.off(EVENTS.AFTER_RESIZE, this.update);
    this._flicking = null;
  }

  public update = (): void => {
    this._onMove();
  };

  private _onMove = (): void => {
    const flicking = this._flicking;
    const selector = this._selector;
    const scale = this._scale;
    const rotate =  this._rotate;
    const perspective = this._perspective;

    if (!flicking) return;

    const horizontal = flicking.horizontal;
    const panels = flicking.visiblePanels;

    panels.forEach((panel) => {
      const progress = panel.outsetProgress;
      const el = panel.element;
      const target = selector ? el.querySelector<HTMLElement>(selector)! : el;
      const panelScale = 1 / (Math.abs(progress * scale) + 1);
      const rotateDegree = progress > 0 ? Math.min(90, progress * 100 * rotate) : Math.max(-90, progress * 100 * rotate);
      const [rotateX, rotateY] = horizontal ? [0, rotateDegree] : [rotateDegree, 0];

      target.style.transform = `perspective(${perspective}px) rotateX(${rotateX}deg) rotateY(${rotateY}deg) scale(${panelScale})`;
    });
  };
}

export default Perspective;
import Flicking from "@egjs/flicking";

import Pagination from "../Pagination";

export interface RendererOptions {
  flicking: Flicking;
  pagination: Pagination;
  wrapper: HTMLElement;
}

abstract class Renderer {
  protected _flicking: Flicking;
  protected _pagination: Pagination;
  protected _wrapper: HTMLElement;

  public constructor({
    flicking,
    pagination,
    wrapper
  }: RendererOptions) {
    this._flicking = flicking;
    this._pagination = pagination;
    this._wrapper = wrapper;
  }

  public abstract render(): void;
  public abstract update(index: number): void;
}

export default Renderer;
import { FlickingError } from "@egjs/flicking";

import { PAGINATION } from "../../const";
import { BROWSER } from "../../event";
import { addClass, removeClass } from "../../utils";

import Renderer from "./Renderer";

class BulletRenderer extends Renderer {
  private _childs: HTMLElement[] = [];

  public render() {
    const flicking = this._flicking;
    const pagination = this._pagination;
    const renderBullet = pagination.renderBullet;
    const bulletWrapperClass = `${pagination.classPrefix}-${PAGINATION.BULLET_WRAPPER_SUFFIX}`;
    const bulletClass = `${pagination.classPrefix}-${PAGINATION.BULLET_SUFFIX}`;
    const bulletActiveClass = `${pagination.classPrefix}-${PAGINATION.BULLET_ACTIVE_SUFFIX}`;
    const anchorPoints = flicking.camera.anchorPoints;
    const wrapper = this._wrapper;

    addClass(wrapper, bulletWrapperClass);

    wrapper.innerHTML = anchorPoints
      .map((_, index) => renderBullet(bulletClass, index))
      .join("\n");

    const bullets = [].slice.call(wrapper.children) as HTMLElement[];

    bullets.forEach((bullet, index) => {
      const anchorPoint = anchorPoints[index];

      if (anchorPoint.panel.index === flicking.index) {
        addClass(bullet, bulletActiveClass);
      }

      bullet.addEventListener(BROWSER.MOUSE_DOWN, e => {
        e.stopPropagation();
      });

      bullet.addEventListener(BROWSER.TOUCH_START, e => {
        e.stopPropagation();
      }, { passive: true });

      bullet.addEventListener(BROWSER.CLICK, () => {
        flicking.moveTo(anchorPoint.panel.index)
          .catch(err => {
            if (err instanceof FlickingError) return;
            throw err;
          });
      });
    });

    this._childs = bullets;
  }

  public update(index: number) {
    const flicking = this._flicking;
    const pagination = this._pagination;
    const bullets = this._childs;
    const activeClass = `${pagination.classPrefix}-${PAGINATION.BULLET_ACTIVE_SUFFIX}`;
    const anchorPoints = flicking.camera.anchorPoints;

    if (anchorPoints.length <= 0) return;

    bullets.forEach(bullet => {
      removeClass(bullet, activeClass);
    });

    const anchorOffset = anchorPoints[0].panel.index;
    const activeBullet = bullets[index - anchorOffset];

    addClass(activeBullet, activeClass);
  }
}

export default BulletRenderer;
import { PAGINATION } from "../../const";
import { addClass } from "../../utils";

import Renderer from "./Renderer";

class FractionRenderer extends Renderer {
  public render() {
    const flicking = this._flicking;
    const wrapper = this._wrapper;
    const pagination = this._pagination;
    const fractionWrapperClass = `${pagination.classPrefix}-${PAGINATION.FRACTION_WRAPPER_SUFFIX}`;
    const fractionCurrentClass = `${pagination.classPrefix}-${PAGINATION.FRACTION_CURRENT_SUFFIX}`;
    const fractionTotalClass = `${pagination.classPrefix}-${PAGINATION.FRACTION_TOTAL_SUFFIX}`;

    addClass(wrapper, fractionWrapperClass);

    wrapper.innerHTML = pagination.renderFraction(fractionCurrentClass, fractionTotalClass);

    this.update(flicking.index);
  }

  public update(index: number) {
    const flicking = this._flicking;
    const wrapper = this._wrapper;
    const pagination = this._pagination;
    const fractionCurrentClass = `${pagination.classPrefix}-${PAGINATION.FRACTION_CURRENT_SUFFIX}`;
    const fractionTotalClass = `${pagination.classPrefix}-${PAGINATION.FRACTION_TOTAL_SUFFIX}`;

    const currentWrapper = wrapper.querySelector(`.${fractionCurrentClass}`) as HTMLElement;
    const totalWrapper = wrapper.querySelector(`.${fractionTotalClass}`) as HTMLElement;
    const anchorPoints = flicking.camera.anchorPoints;

    const currentIndex = anchorPoints.length > 0
      ? index - anchorPoints[0].panel.index + 1
      : 0;

    currentWrapper.innerHTML = pagination.fractionCurrentFormat(currentIndex);
    totalWrapper.innerHTML = pagination.fractionTotalFormat(anchorPoints.length);
  }
}

export default FractionRenderer;
import { DIRECTION, FlickingError } from "@egjs/flicking";

import { PAGINATION } from "../../const";
import { BROWSER } from "../../event";
import { addClass, removeClass } from "../../utils";

import Renderer from "./Renderer";

class ScrollBulletRenderer extends Renderer {
  private _bullets: HTMLElement[] = [];
  private _bulletSize: number = 0;
  private _previousIndex: number = -1;
  private _sliderIndex: number = -1;

  public render() {
    const wrapper = this._wrapper;
    const flicking = this._flicking;
    const pagination = this._pagination;
    const renderBullet = pagination.renderBullet;
    const anchorPoints = flicking.camera.anchorPoints;

    const dynamicWrapperClass = `${pagination.classPrefix}-${PAGINATION.SCROLL_WRAPPER_SUFFIX}`;
    const bulletClass = `${pagination.classPrefix}-${PAGINATION.BULLET_SUFFIX}`;
    const sliderClass = `${pagination.classPrefix}-${PAGINATION.SCROLL_SLIDER_SUFFIX}`;
    const uninitClass = `${pagination.classPrefix}-${PAGINATION.SCROLL_UNINIT_SUFFIX}`;

    const sliderEl = document.createElement("div");

    addClass(sliderEl, sliderClass);
    addClass(wrapper, uninitClass);
    addClass(wrapper, dynamicWrapperClass);
    wrapper.appendChild(sliderEl);

    sliderEl.innerHTML = anchorPoints
      .map((_, index) => renderBullet(bulletClass, index))
      .join("\n");

    const bullets = [].slice.call(sliderEl.children) as HTMLElement[];

    bullets.forEach((bullet, index) => {
      const anchorPoint = anchorPoints[index];

      bullet.addEventListener(BROWSER.MOUSE_DOWN, e => {
        e.stopPropagation();
      });

      bullet.addEventListener(BROWSER.TOUCH_START, e => {
        e.stopPropagation();
      }, { passive: true });

      bullet.addEventListener(BROWSER.CLICK, () => {
        flicking.moveTo(anchorPoint.panel.index)
          .catch(err => {
            if (err instanceof FlickingError) return;
            throw err;
          });
      });
    });

    if (bullets.length <= 0) return;

    const bulletStyle = getComputedStyle(bullets[0]);
    const bulletSize = bullets[0].clientWidth + parseFloat(bulletStyle.marginLeft) + parseFloat(bulletStyle.marginRight);

    wrapper.style.width = `${bulletSize * pagination.bulletCount}px`;

    this._bullets = bullets;
    this._bulletSize = bulletSize;

    this.update(this._flicking.index);

    window.requestAnimationFrame(() => {
      removeClass(wrapper, uninitClass);
    });
  }

  public update(index: number) {
    const pagination = this._pagination;
    const flicking = this._flicking;
    const bullets = this._bullets;
    const prevIndex = this._previousIndex;

    const anchorPoints = flicking.camera.anchorPoints;
    const anchorOffset = anchorPoints[0].panel.index;
    const activeIndex = index - anchorOffset;

    if (anchorPoints.length <= 0) return;

    const bulletActiveClass = `${pagination.classPrefix}-${PAGINATION.BULLET_ACTIVE_SUFFIX}`;
    const prevClassPrefix = `${pagination.classPrefix}-${PAGINATION.SCROLL_PREV_SUFFIX}`;
    const nextClassPrefix = `${pagination.classPrefix}-${PAGINATION.SCROLL_NEXT_SUFFIX}`;
    const bulletPrevClass = (offset: number) => `${prevClassPrefix}${offset > 1 ? offset : ""}`;
    const bulletNextClass = (offset: number) => `${nextClassPrefix}${offset > 1 ? offset : ""}`;

    const prevClassRegex = new RegExp(`^${prevClassPrefix}`);
    const nextClassRegex = new RegExp(`^${nextClassPrefix}`);

    bullets.forEach((bullet, idx) => {
      const indexOffset = idx - activeIndex;
      const classList = bullet.className.split(" ");

      for (const className of classList) {
        if (className === bulletActiveClass || prevClassRegex.test(className) || nextClassRegex.test(className)) {
          removeClass(bullet, className);
        }
      }

      if (indexOffset === 0) {
        addClass(bullet, bulletActiveClass);
      } else if (indexOffset > 0) {
        addClass(bullet, bulletNextClass(Math.abs(indexOffset)));
      } else {
        addClass(bullet, bulletPrevClass(Math.abs(indexOffset)));
      }
    });

    pagination.scrollOnChange(activeIndex, {
      total: bullets.length,
      prevIndex: prevIndex,
      sliderIndex: this._sliderIndex,
      direction: activeIndex > prevIndex ? DIRECTION.NEXT : DIRECTION.PREV,
      bullets: [...bullets],
      moveTo: this.moveTo
    });

    this._previousIndex = activeIndex;
  }

  public moveTo = (index: number): void => {
    const pagination = this._pagination;
    const sliderEl = this._wrapper.firstElementChild as HTMLElement;
    const bulletSize = this._bulletSize;
    const wrapperSize = bulletSize * pagination.bulletCount;

    sliderEl.style.transform = `translate(${wrapperSize / 2 - (index + 0.5) * bulletSize}px)`;
    this._sliderIndex = index;
  };
}

export default ScrollBulletRenderer;
import Flicking, { EVENTS, Plugin } from "@egjs/flicking";

import { PAGINATION } from "../const";
import ScrollContext from "../type";

import Renderer from "./renderer/Renderer";
import BulletRenderer from "./renderer/BulletRenderer";
import FractionRenderer from "./renderer/FractionRenderer";
import ScrollBulletRenderer from "./renderer/ScrollBulletRenderer";

export interface PaginationOptions {
  parentEl: HTMLElement | null;
  selector: string;
  type: "bullet" | "fraction" | "scroll";
  classPrefix: string;
  bulletCount: number;
  renderBullet: (className: string, index: number) => string;
  renderFraction: (currentClass: string, totalClass: string) => string;
  fractionCurrentFormat: (index: number) => string;
  fractionTotalFormat: (total: number) => string;
  scrollOnChange: (index: number, ctx: ScrollContext) => any;
}

/**
 * @memberof Flicking.Plugins
 */
class Pagination implements Plugin {
  /* Internal Values */
  private _flicking: Flicking | null = null;
  private _renderer: Renderer;
  private _wrapper: HTMLElement;

  /* Options */
  private _parentEl: PaginationOptions["parentEl"];
  private _selector: PaginationOptions["selector"];
  private _type: PaginationOptions["type"];
  private _classPrefix: PaginationOptions["classPrefix"];
  private _bulletCount: PaginationOptions["bulletCount"];
  private _renderBullet: PaginationOptions["renderBullet"];
  private _renderFraction: PaginationOptions["renderFraction"];
  private _fractionCurrentFormat: PaginationOptions["fractionCurrentFormat"];
  private _fractionTotalFormat: PaginationOptions["fractionTotalFormat"];
  private _scrollOnChange: PaginationOptions["scrollOnChange"];

  public get parentEl() { return this._parentEl; }
  public get selector() { return this._selector; }
  public get type() { return this._type; }
  public get classPrefix() { return this._classPrefix; }
  public get bulletCount() { return this._bulletCount; }
  public get renderBullet() { return this._renderBullet; }
  public get renderFraction() { return this._renderFraction; }
  public get fractionCurrentFormat() { return this._fractionCurrentFormat; }
  public get fractionTotalFormat() { return this._fractionTotalFormat; }
  public get scrollOnChange() { return this._scrollOnChange; }

  public set parentEl(val: PaginationOptions["parentEl"]) { this._parentEl = val; }
  public set selector(val: PaginationOptions["selector"]) { this._selector = val; }
  public set type(val: PaginationOptions["type"]) { this._type = val; }
  public set bulletWrapperclassPrefixClass(val: PaginationOptions["classPrefix"]) { this._classPrefix = val; }
  public set bulletCount(val: PaginationOptions["bulletCount"]) { this._bulletCount = val; }
  public set renderBullet(val: PaginationOptions["renderBullet"]) { this._renderBullet = val; }
  public set renderFraction(val: PaginationOptions["renderFraction"]) { this._renderFraction = val; }
  public set fractionCurrentFormat(val: PaginationOptions["fractionCurrentFormat"]) { this._fractionCurrentFormat = val; }
  public set fractionTotalFormat(val: PaginationOptions["fractionTotalFormat"]) { this._fractionTotalFormat = val; }
  public set scrollOnChange(val: PaginationOptions["scrollOnChange"]) { this._scrollOnChange = val; }

  public constructor({
    parentEl = null,
    selector = PAGINATION.SELECTOR,
    type = PAGINATION.TYPE.BULLET,
    classPrefix = PAGINATION.PREFIX,
    bulletCount = 5,
    renderBullet = (className: string) => `<span class="${className}"></span>`,
    renderFraction = (currentClass: string, totalClass: string) => `<span class="${currentClass}"></span>/<span class="${totalClass}"></span>`,
    fractionCurrentFormat = (index: number) => index.toString(),
    fractionTotalFormat = (index: number) => index.toString(),
    scrollOnChange = (index: number, ctx: ScrollContext) => ctx.moveTo(index)
  }: Partial<PaginationOptions> = {}) {
    this._parentEl = parentEl;
    this._selector = selector;
    this._type = type;
    this._classPrefix = classPrefix;
    this._bulletCount = bulletCount;
    this._renderBullet = renderBullet;
    this._renderFraction = renderFraction;
    this._fractionCurrentFormat = fractionCurrentFormat;
    this._fractionTotalFormat = fractionTotalFormat;
    this._scrollOnChange = scrollOnChange;
  }

  public init(flicking: Flicking): void {
    if (this._flicking) {
      this.destroy();
    }

    this._flicking = flicking;

    const type = this._type;
    const selector = this._selector;
    const parentEl = this._parentEl ? this._parentEl : flicking.element;
    const wrapper = parentEl.querySelector(selector);

    if (!wrapper) {
      throw new Error(`[Flicking-Pagination] Couldn't find element with the given selector: ${selector}`);
    }

    this._wrapper = wrapper as HTMLElement;
    this._renderer = this._createRenderer(type);

    flicking.on(EVENTS.WILL_CHANGE, this._onIndexChange);
    flicking.on(EVENTS.WILL_RESTORE, this._onIndexChange);
    flicking.on(EVENTS.PANEL_CHANGE, this.update);

    this.update();
  }

  public destroy(): void {
    const flicking = this._flicking;

    if (!flicking) {
      return;
    }

    flicking.off(EVENTS.WILL_CHANGE, this._onIndexChange);
    flicking.off(EVENTS.WILL_RESTORE, this._onIndexChange);
    flicking.off(EVENTS.PANEL_CHANGE, this.update);

    this._removeAllChilds();
    this._flicking = null;
  }

  public update = (): void => {
    this._removeAllChilds();
    this._renderer.render();
  };

  private _createRenderer(type: PaginationOptions["type"]) {
    const options = {
      flicking: this._flicking!,
      pagination: this,
      wrapper: this._wrapper
    };

    switch (type) {
      case PAGINATION.TYPE.BULLET:
        return new BulletRenderer(options);
      case PAGINATION.TYPE.FRACTION:
        return new FractionRenderer(options);
      case PAGINATION.TYPE.SCROLL:
        return new ScrollBulletRenderer(options);
      default:
        throw new Error(`[Flicking-Pagination] type "${type}" is not supported.`);
    }
  }

  private _onIndexChange = (evt: { index: number }) => {
    this._renderer.update(evt.index);
  };

  private _removeAllChilds() {
    const wrapper = this._wrapper;

    while (wrapper.firstChild) {
      wrapper.removeChild(wrapper.firstChild);
    }
  }
}

export default Pagination;
