import { ElementProvider } from "@egjs/flicking";

import VuePanel from "./VuePanel";

class VueElementProvider implements ElementProvider {
  private _el: VuePanel;

  public get element() { return this._el.getElement(); }
  public get rendered() { return !this._el.hide; }

  public constructor(el: VuePanel) {
    this._el = el;
  }

  public show() {
    this._el.hide = false;
  }

  public hide() {
    this._el.hide = true;
  }
}

export default VueElementProvider;
import { PropType } from "vue";
import { RecordPropsDefinition } from "vue/types/options";
import { FlickingOptions, Plugin, Status } from "@egjs/flicking";

export default {
  viewportTag: {
    type: String,
    default: "div",
    required: false
  },
  cameraTag: {
    type: String,
    default: "div",
    required: false
  },
  hideBeforeInit: {
    type: Boolean,
    default: false,
    required: false
  },
  firstPanelSize: {
    type: String,
    required: false
  },
  options: {
    type: Object as PropType<Partial<FlickingOptions>>,
    default: () => ({}),
    required: false
  },
  plugins: {
    type: Array as PropType<Plugin[]>,
    default: () => ([]),
    required: false
  },
  status: {
    type: Object as PropType<Status>,
    required: false
  }
} as RecordPropsDefinition<{
  readonly viewportTag: string;
  readonly cameraTag: string;
  readonly hideBeforeInit: boolean;
  readonly firstPanelSize: string;
  readonly options: Partial<FlickingOptions>;
  readonly plugins: Plugin[];
  readonly status: Status;
}>;

import Vue from "vue";

export const getSlots = (component: Vue) => {
  return component.$slots.default?.filter(slot => slot.tag) ?? [];
};

export const fillKeys = (component: Vue) => {
  const vnodes = getSlots(component);

  vnodes.forEach((node, idx) => {
    if (node.key == null) {
      node.key = `$_${idx}`;
    }
  });
};
