<script>
  export default {
    name: "App",
    methods: {
      scrollToTop(){
        let currentScroll = document.documentElement.scrollTop,
          int = setInterval(frame, 4)
          function frame(){
              if( 0 < currentScroll)
                  clearInterval(int)
              else {
                  currentScroll = currentScroll + 1200
                  document.documentElement.scrollTop = currentScroll
              }
          }
      }
    },
  }
</script>

